t.script:
document.addEventListener('click', (e) => {
  if(e.target && ["add_to_cart", "buy_now"].includes(e.target.id)){
    fbq('track', 'AddToCart', {
      content_name: '',
      content_category: '',
      content_ids: ,
      content_type: 'Product',
      value: ,
      content_name: '',
    });
  }          
});
